import { AnimationLooper } from './Loopers'
import Spring from './Spring'
import { removeFirst } from './util'

/**
 * A set of Springs that all run on the same physics
 * timing loop. To get started with a Rebound animation, first
 * create a new SpringSystem and then add springs to it.
 * @public
 */
class SpringSystem {
  constructor(looper) {
    this.looper = looper || new AnimationLooper()
    this.looper.springSystem = this

    this.listeners = []
    this._activeSprings = []
    this._idleSpringIndices = []
    this._isIdle = true
    this._lastTimeMillis = -1
    this._springRegistry = {}
  }

  /**
   * Add a new spring to this SpringSystem. This Spring will now be solved for
   * during the physics iteration loop. By default the spring will use the
   * default Origami spring config with 40 tension and 7 friction, but you can
   * also provide your own values here.
   * @public
   */
  createSpring(tension, friction) {
    return this.createSpringWithConfig({ tension, friction })
  }
  /**
   * Add a spring with the provided SpringConfig.
   * @public
   */
  createSpringWithConfig(springConfig) {
    const spring = new Spring(this)
    this.registerSpring(spring)
    spring.setSpringConfig(springConfig)
    return spring
  }

  /**
   * Check if a SpringSystem is idle or active. If all of the Springs in the
   * SpringSystem are at rest, i.e. the physics forces have reached equilibrium,
   * then this method will return true.
   * @public
   */
  getIsIdle() {
    return this._isIdle
  }

  /**
   * Manually add a spring to this system. This is called automatically
   * if a Spring is created with SpringSystem#createSpring.
   *
   * This method sets the spring up in the registry so that it can be solved
   * in the solver loop.
   * @public
   */
  registerSpring(spring) {
    this._springRegistry[spring.getId()] = spring
  }

  /**
   * Deregister a spring with this SpringSystem. The SpringSystem will
   * no longer consider this Spring during its integration loop once
   * this is called. This is normally done automatically for you when
   * you call Spring#destroy.
   * @public
   */
  deregisterSpring(spring) {
    removeFirst(this._activeSprings, spring)
    delete this._springRegistry[spring.getId()]
  }

  advance(time, deltaTime) {
    while (this._idleSpringIndices.length > 0) {
      this._idleSpringIndices.pop()
    }
    this._activeSprings.filter(Boolean).forEach(spring => {
      if (spring.systemShouldAdvance()) {
        spring.advance(time / 1000.0, deltaTime / 1000.0)
      } else {
        this._idleSpringIndices.push(this._activeSprings.indexOf(spring))
      }
    })

    while (this._idleSpringIndices.length > 0) {
      const idx = this._idleSpringIndices.pop()
      idx >= 0 && this._activeSprings.splice(idx, 1)
    }
  }

  /**
   * This is the main solver loop called to move the simulation
   * forward through time. Before each pass in the solver loop
   * onBeforeIntegrate is called on an any listeners that have
   * registered themeselves with the SpringSystem. This gives you
   * an opportunity to apply any constraints or adjustments to
   * the springs that should be enforced before each iteration
   * loop. Next the advance method is called to move each Spring in
   * the systemShouldAdvance forward to the current time. After the
   * integration step runs in advance, onAfterIntegrate is called
   * on any listeners that have registered themselves with the
   * SpringSystem. This gives you an opportunity to run any post
   * integration constraints or adjustments on the Springs in the
   * SpringSystem.
   * @public
   */
  loop(currentTimeMillis) {
    let listener
    if (this._lastTimeMillis === -1) {
      this._lastTimeMillis = currentTimeMillis - 1
    }
    const ellapsedMillis = currentTimeMillis - this._lastTimeMillis
    this._lastTimeMillis = currentTimeMillis

    let i = 0
    const len = this.listeners.length
    for (i = 0; i < len; i++) {
      listener = this.listeners[i]
      listener.onBeforeIntegrate && listener.onBeforeIntegrate(this)
    }

    this.advance(currentTimeMillis, ellapsedMillis)
    if (this._activeSprings.length === 0) {
      this._isIdle = true
      this._lastTimeMillis = -1
    }

    for (i = 0; i < len; i++) {
      listener = this.listeners[i]
      listener.onAfterIntegrate && listener.onAfterIntegrate(this)
    }

    if (!this._isIdle) {
      this.looper.run()
    }
  }

  /**
   * Used to notify the SpringSystem that a Spring has become displaced.
   * The system responds by starting its solver loop up if it is currently idle.
   */
  activateSpring(springId) {
    const spring = this._springRegistry[springId]
    if (this._activeSprings.indexOf(spring) === -1) {
      this._activeSprings.push(spring)
    }
    if (this.getIsIdle()) {
      this._isIdle = false
      this.looper.run()
    }
  }
}

export default SpringSystem
import { SpringSystem } from '../../../forked-rebound'
import { StaggerConfigValue } from '../../../types'
import { FlipData, FlipDataArray } from '../types'
import {
  SpringSystemInterface,
  AddListenerArgs
} from '../../../forked-rebound/types'

// this should get created only 1x
const springSystem: SpringSystemInterface = new SpringSystem()

export const createSuspendedSpring = (flipData: FlipData) => {
  const {
    springConfig: { stiffness, damping, overshootClamping },
    getOnUpdateFunc,
    onAnimationEnd,
    onSpringActivate
  } = flipData

  const spring = springSystem.createSpring(stiffness!, damping!)
  spring.setOvershootClampingEnabled(!!overshootClamping)
  const onSpringAtRest = () => {
    // prevent SpringSystem from caching unused springs
    spring.destroy()
    onAnimationEnd()
  }

  const springConfig: AddListenerArgs = {
    onSpringActivate,
    onSpringAtRest,
    onSpringUpdate: getOnUpdateFunc({
      spring,
      onAnimationEnd
    })
  }

  spring.addListener(springConfig)
  return spring
}

export const createSpring = (flipped: FlipData) => {
  const spring = createSuspendedSpring(flipped)
  spring.setEndValue(1)
  return spring
}

export const normalizeSpeed = (speedConfig: number | undefined) => {
  if (typeof speedConfig !== 'number') return 1.1
  return 1 + Math.min(Math.max(speedConfig * 5, 0), 5)
}

export const createStaggeredSprings = (
  flippedArray: FlipDataArray,
  staggerConfig: StaggerConfigValue = {}
) => {
  if (!flippedArray || !flippedArray.length) {
    return
  }

  if (staggerConfig.reverse) {
    flippedArray.reverse()
  }

  const normalizedSpeed = normalizeSpeed(staggerConfig.speed)

  const nextThreshold = 1 / Math.max(Math.min(flippedArray.length, 100), 10)

  const setEndValueFuncs = flippedArray
    .map((flipped, i) => {
      const cachedGetOnUpdate = flipped.getOnUpdateFunc

      // modify the update function to adjust
      // the end value of the trailing Flipped component
      flipped.getOnUpdateFunc = args => {
        const onUpdate = cachedGetOnUpdate(args)
        return spring => {
          let currentValue = spring.getCurrentValue()
          // make sure trailing animations complete
          currentValue =
            currentValue < 0.01 ? 0 : currentValue > 0.99 ? 1 : currentValue

          const updateTrailingAnimation = currentValue >= nextThreshold
          if (updateTrailingAnimation) {
            if (setEndValueFuncs[i + 1]) {
              setEndValueFuncs[i + 1]!(
                Math.max(Math.min(currentValue * normalizedSpeed, 1), 0)
              )
            }
          }
          // now call the actual update function
          onUpdate(spring)
        }
      }
      return flipped
    })
    .map(flipped => {
      const spring = createSuspendedSpring(flipped)
      if (!spring) {
        return
      }
      return spring.setEndValue.bind(spring)
    })
    .filter(Boolean)

  if (setEndValueFuncs[0]) {
    setEndValueFuncs[0]!(1)
  }
}
import * as Rematrix from 'rematrix'
import { getSpringConfig } from '../../springSettings'
import {
  toArray,
  isFunction,
  isNumber,
  getDuplicateValsAsStrings,
  assign,
  tweenProp
} from '../../utilities'
import * as constants from '../../constants'
import {
  GetOnUpdateFunc,
  OnUpdate,
  Matrix,
  InvertedChildren,
  AnimateFlippedElementsArgs,
  AnimatedVals,
  FlipDataArray,
  FlipData,
  InitializeFlip
} from './types'
import { BoundingClientRect } from '../getFlippedElementPositions/types'
import { FlippedIds } from '../types'
import { createSpring, createStaggeredSprings } from './spring'
import { IndexableObject } from '../../utilities/types'
import { FlipId } from '../../types'

// 3d transforms were causing weird issues in chrome,
// especially when opacity was also being tweened,
// so convert to a 2d matrix
export const convertMatrix3dArrayTo2dArray = (matrix: Matrix): Matrix =>
  [0, 1, 4, 5, 12, 13].map(index => matrix[index])

export const convertMatrix2dArrayToString = (matrix: Matrix) =>
  `matrix(${matrix.join(', ')})`

export const invertTransformsForChildren = ({
  invertedChildren,
  matrix,
  body
}: {
  matrix: Matrix
  body: HTMLBodyElement
  invertedChildren: InvertedChildren
}) => {
  invertedChildren.forEach(([child, childFlipConfig]) => {
    if (!body.contains(child)) {
      return
    }
    const scaleX = matrix[0]
    const scaleY = matrix[3]
    const translateX = matrix[4]
    const translateY = matrix[5]

    const inverseVals = { translateX: 0, translateY: 0, scaleX: 1, scaleY: 1 }
    let transformString = ''
    if (childFlipConfig.translate) {
      inverseVals.translateX = -translateX / scaleX
      inverseVals.translateY = -translateY / scaleY
      transformString += `translate(${inverseVals.translateX}px, ${inverseVals.translateY}px)`
    }
    if (childFlipConfig.scale) {
      inverseVals.scaleX = 1 / scaleX
      inverseVals.scaleY = 1 / scaleY
      transformString += ` scale(${inverseVals.scaleX}, ${inverseVals.scaleY})`
    }
    child.style.transform = transformString
  })
}

export const createApplyStylesFunc =
  ({
    element,
    invertedChildren,
    body
  }: {
    element: HTMLElement
    invertedChildren: InvertedChildren
    body: HTMLBodyElement
  }) =>
  ({
    matrix,
    opacity,
    forceMinVals
  }: {
    matrix: Matrix
    opacity?: number
    forceMinVals?: boolean
  }) => {
    if (isNumber(opacity)) {
      element.style.opacity = opacity + ''
    }

    if (forceMinVals) {
      element.style.minHeight = '1px'
      element.style.minWidth = '1px'
    }

    if (!matrix) {
      return
    }

    const stringTransform = convertMatrix2dArrayToString(matrix)

    // always apply transform, even if identity,
    // because identity might be the starting state in a FLIP
    // transition, if the element's position is controlled by transforms
    element.style.transform = stringTransform

    if (invertedChildren) {
      invertTransformsForChildren({
        invertedChildren,
        matrix,
        body
      })
    }
  }

export const rectInViewport = ({
  top,
  bottom,
  left,
  right
}: BoundingClientRect) => {
  return (
    top < window.innerHeight &&
    bottom > 0 &&
    left < window.innerWidth &&
    right > 0
  )
}

const getInvertedChildren = (element: HTMLElement, id: string) =>
  toArray(
    element.querySelectorAll(`[${constants.DATA_INVERSE_FLIP_ID}="${id}"]`)
  )

function extractFlipConfig(element: HTMLElement) {
  const flipConfig = JSON.parse(element.dataset.flipConfig || '{}')
  return flipConfig
}

export default ({
  flippedIds,
  flipCallbacks,
  inProgressAnimations,
  flippedElementPositionsBeforeUpdate,
  flippedElementPositionsAfterUpdate,
  applyTransformOrigin,
  spring,
  getElement,
  debug,
  staggerConfig = {},
  decisionData = {},
  onComplete,
  containerEl
}: AnimateFlippedElementsArgs) => {
  // the stuff below is used so we can return a promise that resolves when all FLIP animations have
  // completed
  let closureResolve: (flipIds: FlippedIds) => void

  const flipCompletedPromise: Promise<FlippedIds> = new Promise(resolve => {
    closureResolve = resolve
  })
  // hook for users of lib to attach logic when all flip animations have completed
  if (onComplete) {
    flipCompletedPromise.then(() => onComplete(containerEl, decisionData))
  }
  if (!flippedIds.length) {
    return () => {
      closureResolve!([])
      return flipCompletedPromise
    }
  }

  const completedAnimationIds: FlippedIds = []

  const firstElement: HTMLElement = getElement(flippedIds[0])
  // special handling for iframes
  const body = firstElement
    ? firstElement.ownerDocument!.querySelector('body')!
    : document.querySelector('body')!

  if (process.env.NODE_ENV !== 'production') {
    if (debug) {
      // eslint-disable-next-line no-console
      console.error(
        '[react-flip-toolkit]\nThe "debug" prop is set to true. All FLIP animations will return at the beginning of the transition.'
      )
    }
  }

  const duplicateFlipIds = getDuplicateValsAsStrings(flippedIds)
  if (process.env.NODE_ENV !== 'production') {
    if (duplicateFlipIds.length) {
      // eslint-disable-next-line no-console
      console.error(
        `[react-flip-toolkit]\nThere are currently multiple elements with the same flipId on the page.\nThe animation will only work if each Flipped component has a unique flipId.\nDuplicate flipId${
          duplicateFlipIds.length > 1 ? 's' : ''
        }: ${duplicateFlipIds.join('\n')}`
      )
    }
  }

  const flipDataArray: FlipDataArray = flippedIds

    // take all the measurements we need
    // and return an object with animation functions + necessary data
    .map(id => {
      const prevRect = flippedElementPositionsBeforeUpdate[id].rect
      const currentRect = flippedElementPositionsAfterUpdate[id].rect
      const prevOpacity = flippedElementPositionsBeforeUpdate[id].opacity
      const currentOpacity = flippedElementPositionsAfterUpdate[id].opacity
      const needsForcedMinVals = currentRect.width < 1 || currentRect.height < 1
      const element = flippedElementPositionsAfterUpdate[id].element

      // don't animate elements outside of the user's viewport
      if (!rectInViewport(prevRect) && !rectInViewport(currentRect)) {
        return false
      }

      // this might happen if we are rapidly adding & removing elements(?)
      if (!element) {
        return false
      }

      const flipConfig = extractFlipConfig(element)

      const springConfig = getSpringConfig({
        flipperSpring: spring,
        flippedSpring: flipConfig.spring
      })

      const stagger =
        flipConfig.stagger === true ? 'default' : flipConfig.stagger

      const toReturn = {
        element,
        id,
        stagger,
        springConfig
      }

      if (flipCallbacks[id] && flipCallbacks[id].shouldFlip) {
        const elementShouldFlip = flipCallbacks[id].shouldFlip!(
          decisionData.previous,
          decisionData.current
        )
        if (!elementShouldFlip) {
          return false
        }
      }

      // don't animate elements that didn't visibly change
      // but possibly animate their children

      const translateXDifference = Math.abs(prevRect.left - currentRect.left)
      const translateYDifference = Math.abs(prevRect.top - currentRect.top)

      const translateDifference = translateXDifference + translateYDifference

      const scaleXDifference = Math.abs(prevRect.width - currentRect.width)
      const scaleYDifference = Math.abs(prevRect.height - currentRect.height)

      const scaleDifference = scaleXDifference + scaleYDifference

      const opacityDifference = Math.abs(currentOpacity - prevOpacity)
      const differenceTooSmall =
        translateDifference < 0.5 &&
        scaleDifference < 0.5 &&
        opacityDifference < 0.01

      const hiddenDueToDimensions =
        (prevRect.height === 0 && currentRect.height === 0) ||
        (prevRect.width === 0 && currentRect.width === 0)
      if (hiddenDueToDimensions || differenceTooSmall) {
        return false
      }

      const currentTransform = Rematrix.parse(
        flippedElementPositionsAfterUpdate[id].transform
      )

      const toVals: AnimatedVals = { matrix: currentTransform }

      const fromVals: AnimatedVals = { matrix: [] }
      const transformsArray = [currentTransform]

      // we're only going to animate the values that the child wants animated
      if (flipConfig.translate) {
        transformsArray.push(
          Rematrix.translateX(prevRect.left - currentRect.left)
        )
        transformsArray.push(
          Rematrix.translateY(prevRect.top - currentRect.top)
        )
      }
      // going any smaller than 1px breaks transitions in Chrome
      if (flipConfig.scale) {
        transformsArray.push(
          Rematrix.scaleX(
            Math.max(prevRect.width, 1) / Math.max(currentRect.width, 1)
          )
        )
        transformsArray.push(
          Rematrix.scaleY(
            Math.max(prevRect.height, 1) / Math.max(currentRect.height, 1)
          )
        )
      }

      if (flipConfig.opacity) {
        fromVals.opacity = prevOpacity
        toVals.opacity = currentOpacity
      }

      let invertedChildren: InvertedChildren = []

      if (
        !flipCallbacks[id] ||
        !flipCallbacks[id].shouldInvert ||
        flipCallbacks[id].shouldInvert!(
          decisionData.previous,
          decisionData.current
        )
      ) {
        const invertedChildElements = getInvertedChildren(element, id)
        invertedChildren = invertedChildElements.map(c => [
          c,
          extractFlipConfig(c)
        ]) as InvertedChildren
      }

      fromVals.matrix = convertMatrix3dArrayTo2dArray(
        transformsArray.reduce(Rematrix.multiply)
      )

      toVals.matrix = convertMatrix3dArrayTo2dArray(toVals.matrix)

      const applyStyles = createApplyStylesFunc({
        element,
        invertedChildren,
        body
      })

      let onComplete: () => void
      if (flipCallbacks[id] && flipCallbacks[id].onComplete) {
        // must cache or else this could cause an error
        const cachedOnComplete = flipCallbacks[id].onComplete
        onComplete = () => cachedOnComplete!(element, decisionData)
      }

      // this should be called when animation ends naturally
      // but also when it is interrupted
      // when it is called, the animation has already been cancelled
      const onAnimationEnd = (isCancellation: boolean) => {
        delete inProgressAnimations[id]
        if (isFunction(onComplete)) {
          onComplete()
        }
        // remove identity transform -- this should have no effect on layout
        element.style.transform = ''
        invertedChildren.forEach(([element]) => {
          element.style.transform = ''
        })
        if (needsForcedMinVals && element) {
          element.style.minHeight = ''
          element.style.minWidth = ''
        }
        if (isCancellation) return

        completedAnimationIds.push(id)

        if (completedAnimationIds.length >= flipDataArray.length) {
          // we can theoretically call multiple times since a promise only resolves 1x
          // but that shouldnt happen
          closureResolve(completedAnimationIds)
        }
      }

      const animateOpacity =
        isNumber(fromVals.opacity) &&
        isNumber(toVals.opacity) &&
        fromVals.opacity !== toVals.opacity

      let onStartCalled = false

      const getOnUpdateFunc: GetOnUpdateFunc = ({ spring, onAnimationEnd }) => {
        inProgressAnimations[id] = {
          destroy: spring.destroy.bind(spring),
          onAnimationEnd
        }
        const onUpdate: OnUpdate = spring => {
          if (flipCallbacks[id] && flipCallbacks[id].onSpringUpdate) {
            flipCallbacks[id].onSpringUpdate!(spring.getCurrentValue())
          }
          // trigger the user provided onStart function
          if (!onStartCalled) {
            onStartCalled = true
            if (flipCallbacks[id] && flipCallbacks[id].onStart) {
              flipCallbacks[id].onStart!(element, decisionData)
            }
          }

          const currentValue = spring.getCurrentValue()
          if (!body.contains(element)) {
            spring.destroy()
            return
          }

          const vals: AnimatedVals = { matrix: [] }

          vals.matrix = fromVals.matrix.map((fromVal, index) =>
            tweenProp(fromVal, toVals.matrix[index], currentValue)
          )

          if (animateOpacity) {
            vals.opacity = tweenProp(
              fromVals.opacity!,
              toVals.opacity!,
              currentValue
            )
          }
          applyStyles(vals)
        }
        return onUpdate
      }

      const initializeFlip: InitializeFlip = () => {
        // before animating, immediately apply FLIP styles to prevent flicker
        applyStyles({
          matrix: fromVals.matrix,
          opacity: animateOpacity ? fromVals.opacity : undefined,
          forceMinVals: needsForcedMinVals
        })

        if (flipCallbacks[id] && flipCallbacks[id].onStartImmediate) {
          flipCallbacks[id].onStartImmediate!(element, decisionData)
        }
        // and batch any other style updates if necessary
        if (flipConfig.transformOrigin) {
          element.style.transformOrigin = flipConfig.transformOrigin
        } else if (applyTransformOrigin) {
          element.style.transformOrigin = '0 0'
        }

        invertedChildren.forEach(([child, childFlipConfig]) => {
          if (childFlipConfig.transformOrigin) {
            child.style.transformOrigin = childFlipConfig.transformOrigin
          } else if (applyTransformOrigin) {
            child.style.transformOrigin = '0 0'
          }
        })
      }

      return assign({}, toReturn, {
        stagger,
        springConfig,
        getOnUpdateFunc,
        initializeFlip,
        onAnimationEnd,
        delayUntil: flipConfig.delayUntil
      }) as FlipData
    })
    // filter out data for all non-animated elements first
    .filter(Boolean) as FlipDataArray

  flipDataArray.forEach(({ initializeFlip }) => initializeFlip())

  if (debug) {
    return () => {}
  }

  const elementIsFlipped = (flipId: FlipId) =>
    flipDataArray.filter(f => f.id === flipId).length

  const delayedFlip = flipDataArray.filter(
    f => f.delayUntil && elementIsFlipped(f.delayUntil)
  )

  // key: flipId value: flip to delay until key is called
  const delayUntilSprings = {} as IndexableObject
  // key: flipId value: stagger to delay until key is called
  const delayUntilStaggers = {} as IndexableObject
  // key: stagger value: true
  const delayedStaggerKeys = {} as IndexableObject

  delayedFlip.forEach(flip => {
    if (flip.stagger) {
      delayedStaggerKeys[flip.stagger] = true
      if (delayUntilStaggers[flip.delayUntil!])
        delayUntilStaggers[flip.delayUntil!].push(flip.stagger)
      else delayUntilStaggers[flip.delayUntil!] = [flip.stagger]
    } else {
      if (delayUntilSprings[flip.delayUntil!])
        delayUntilSprings[flip.delayUntil!].push(flip)
      else delayUntilSprings[flip.delayUntil!] = [flip]
    }
  })

  const staggerDict = flipDataArray
    .filter(flipData => flipData.stagger)
    .reduce((acc, curr) => {
      if (acc[curr.stagger]) {
        acc[curr.stagger].push(curr)
      } else {
        acc[curr.stagger] = [curr]
      }
      return acc
    }, {} as IndexableObject)

  const immediateFlip = flipDataArray.filter(f => delayedFlip.indexOf(f) === -1)

  immediateFlip.forEach(flipData => {
    flipData.onSpringActivate = () => {
      if (delayUntilSprings[flipData.id]) {
        delayUntilSprings[flipData.id].forEach(createSpring)
      }
      if (delayUntilStaggers[flipData.id]) {
        const uniqueStaggerKeys = Object.keys(
          delayUntilStaggers[flipData.id].reduce(
            (acc: IndexableObject, curr: string) =>
              assign(acc, { [curr]: true }),
            {}
          )
        )
        uniqueStaggerKeys.forEach((staggerKey: string) => {
          createStaggeredSprings(
            staggerDict[staggerKey],
            staggerConfig[staggerKey]
          )
        })
      }
    }
  })

  return () => {
    // if there are no active FLIP animations, immediately resolve the
    // returned promise
    if (!flipDataArray.length) {
      closureResolve([])
    }
    // animate non-staggered elements
    immediateFlip
      .filter(flipData => {
        return !flipData.stagger
      })
      .forEach(createSpring)

    // animate staggered elements
    Object.keys(staggerDict).forEach(staggerKey => {
      if (delayedStaggerKeys[staggerKey]) return
      createStaggeredSprings(staggerDict[staggerKey], staggerConfig[staggerKey])
    })
    return flipCompletedPromise
  }
}
import { toArray, assign } from '../../utilities'
import * as constants from '../../constants'
import { BoundingClientRect } from './types'

export const addTupleToObject = <T>(
  acc: Record<string, T>,
  curr: [string, T]
): Record<string, T> => assign(acc, { [curr[0]]: curr[1] })

export const getAllElements = (
  element?: HTMLElement,
  portalKey?: string
): HTMLElement[] => {
  if (portalKey) {
    return toArray(
      document.querySelectorAll(`[${constants.DATA_PORTAL_KEY}="${portalKey}"]`)
    )
  } else {
    return toArray(element!.querySelectorAll(`[${constants.DATA_FLIP_ID}]`))
  }
}
export const getRects = (
  flippedElements: HTMLElement[]
): [HTMLElement, BoundingClientRect][] => {
  return flippedElements.map(
    (child: HTMLElement): [HTMLElement, BoundingClientRect] => [
      child,
      child.getBoundingClientRect()
    ]
  )
}
import animateUnflippedElements from './animateUnflippedElements'
import animateFlippedElements from './animateFlippedElements'
import getFlippedElementPositionsAfterUpdate from './getFlippedElementPositions/getFlippedElementPositionsAfterUpdate'
import * as constants from '../constants'
import { assign, toArray } from '../utilities'
import {
  GetElement,
  BaseFlipArgs,
  OnFlipKeyUpdateArgs,
  FlippedIds
} from './types'
import { AnimateUnflippedElementsArgs } from './animateUnflippedElements/types'
import {
  AnimateFlippedElementsArgs,
  ScopedSelector
} from './animateFlippedElements/types'

const createPortalScopedSelector =
  (portalKey: string) => (selector: string) => {
    return toArray(
      document.querySelectorAll(
        `[${constants.DATA_PORTAL_KEY}="${portalKey}"]${selector}`
      )
    )
  }
const createFlipperScopedSelector = (containerEl: HTMLElement) => {
  const tempFlipperId = Math.random().toFixed(5)
  containerEl.dataset.flipperId = tempFlipperId

  return (selector: string) => {
    return toArray(
      containerEl.querySelectorAll(
        `[data-flipper-id="${tempFlipperId}"] ${selector}`
      )
    )
  }
}
const createScopedSelector = ({
  containerEl,
  portalKey
}: {
  containerEl?: HTMLElement
  portalKey?: string
}): ScopedSelector => {
  if (portalKey) {
    return createPortalScopedSelector(portalKey)
  } else if (containerEl) {
    return createFlipperScopedSelector(containerEl)
  } else {
    return () => []
  }
}

const createGetElementFunc = (scopedSelector: ScopedSelector): GetElement => {
  return (id: string) => {
    return scopedSelector(`[${constants.DATA_FLIP_ID}="${id}"]`)[0]
  }
}

const onFlipKeyUpdate = ({
  cachedOrderedFlipIds = [],
  inProgressAnimations = {},
  flippedElementPositionsBeforeUpdate = {},
  flipCallbacks = {},
  containerEl,
  applyTransformOrigin,
  spring,
  debug,
  portalKey,
  staggerConfig = {},
  decisionData = {},
  handleEnterUpdateDelete,
  onComplete,
  onStart
}: OnFlipKeyUpdateArgs) => {
  const mediaQuery = window.matchMedia('(prefers-reduced-motion: reduce)')
  if (mediaQuery.matches) return
  const flippedElementPositionsAfterUpdate =
    getFlippedElementPositionsAfterUpdate({
      element: containerEl,
      portalKey
    })

  const scopedSelector = createScopedSelector({
    containerEl,
    portalKey
  })
  const getElement = createGetElementFunc(scopedSelector)

  const isFlipped = (id: string) =>
    flippedElementPositionsBeforeUpdate[id] &&
    flippedElementPositionsAfterUpdate[id]

  const unflippedIds = Object.keys(flippedElementPositionsBeforeUpdate)
    .concat(Object.keys(flippedElementPositionsAfterUpdate))
    .filter(id => !isFlipped(id))

  const baseArgs: BaseFlipArgs = {
    flipCallbacks,
    getElement,
    flippedElementPositionsBeforeUpdate,
    flippedElementPositionsAfterUpdate,
    inProgressAnimations,
    decisionData
  }

  const animateUnFlippedElementsArgs = assign({}, baseArgs, {
    unflippedIds
  }) as AnimateUnflippedElementsArgs

  const {
    hideEnteringElements,
    animateEnteringElements,
    animateExitingElements
  } = animateUnflippedElements(animateUnFlippedElementsArgs)

  const flippedIds: FlippedIds = cachedOrderedFlipIds.filter(isFlipped)
  // @ts-ignore
  const animateFlippedElementsArgs: AnimateFlippedElementsArgs = assign(
    {},
    baseArgs,
    {
      containerEl,
      flippedIds,
      applyTransformOrigin,
      spring,
      debug,
      staggerConfig,
      scopedSelector,
      onComplete
    }
  )
  if (onStart) onStart(containerEl, decisionData)

  // the function handles putting flipped elements back in their original positions
  // and returns another function to actually call the flip animation
  const flip = animateFlippedElements(animateFlippedElementsArgs)

  if (handleEnterUpdateDelete) {
    handleEnterUpdateDelete({
      hideEnteringElements,
      animateEnteringElements,
      animateExitingElements,
      animateFlippedElements: flip
    })
  } else {
    hideEnteringElements()
    animateExitingElements().then(animateEnteringElements)
    flip()
  }
}

export default onFlipKeyUpdate
import { addTupleToObject, getRects, getAllElements } from '../utilities'
import {
  FlippedElementPositionsAfterUpdate,
  FlippedElementPositionDatumAfterUpdate
} from './types'

const getFlippedElementPositionsAfterUpdate = ({
  element,
  portalKey
}: {
  element: HTMLElement
  portalKey?: string
}): FlippedElementPositionsAfterUpdate => {
  const positionArray = getRects(getAllElements(element, portalKey)).map(
    ([child, childBCR]) => {
      const computedStyle = window.getComputedStyle(child)
      return [
        child.dataset.flipId,
        {
          element: child,
          rect: childBCR,
          opacity: parseFloat(computedStyle.opacity!),
          transform: computedStyle.transform
        }
      ]
    }
  ) as [string, FlippedElementPositionDatumAfterUpdate][]

  return positionArray.reduce(addTupleToObject, {})
}

export default getFlippedElementPositionsAfterUpdate
import { AnimateUnflippedElementsArgs, FragmentTuple } from './types'

const animateUnflippedElements = ({
  unflippedIds,
  flipCallbacks,
  getElement,
  flippedElementPositionsBeforeUpdate,
  flippedElementPositionsAfterUpdate,
  inProgressAnimations,
  decisionData
}: AnimateUnflippedElementsArgs) => {
  const enteringElementIds = unflippedIds.filter(
    id => flippedElementPositionsAfterUpdate[id]
  )
  const animatedEnteringElementIds = enteringElementIds.filter(
    id => flipCallbacks[id] && flipCallbacks[id].onAppear
  )

  const animatedExitingElementIds = unflippedIds.filter(
    id =>
      flippedElementPositionsBeforeUpdate[id] &&
      flipCallbacks[id] &&
      flipCallbacks[id].onExit
  )

  const hideEnteringElements = () => {
    animatedEnteringElementIds.forEach(id => {
      const element = getElement(id)
      if (element) {
        element.style.opacity = '0'
      }
    })
  }

  const animateEnteringElements = () => {
    animatedEnteringElementIds.forEach((id, i) => {
      const element = getElement(id)
      if (element) {
        flipCallbacks[id].onAppear!(element, i, decisionData)
      }
    })
  }

  let closureResolve: () => void

  const promiseToReturn: Promise<void> = new Promise(resolve => {
    closureResolve = resolve
  })

  const fragmentTuples: FragmentTuple[] = []
  let exitingElementCount = 0

  const onExitCallbacks = animatedExitingElementIds.map((id, i) => {
    const {
      domDataForExitAnimations: {
        element,
        parent,
        childPosition: { top, left, width, height }
      }
    } = flippedElementPositionsBeforeUpdate[id]
    // insert back into dom
    if (getComputedStyle(parent).position === 'static') {
      parent.style.position = 'relative'
    }
    element.style.transform = 'matrix(1, 0, 0, 1, 0, 0)'
    element.style.position = 'absolute'
    element.style.top = top + 'px'
    element.style.left = left + 'px'
    // taken out of the dom flow, the element might have lost these dimensions
    element.style.height = height + 'px'
    element.style.width = width + 'px'
    let fragmentTuple: FragmentTuple | undefined = fragmentTuples.filter(
      t => t[0] === parent
    )[0]
    if (!fragmentTuple) {
      fragmentTuple = [parent, document.createDocumentFragment()]
      fragmentTuples.push(fragmentTuple)
    }
    fragmentTuple[1].appendChild(element)

    exitingElementCount += 1

    const stop = () => {
      try {
        parent.removeChild(element)
      } catch (DOMException) {
        // the element is already gone
      } finally {
        exitingElementCount -= 1
        if (exitingElementCount === 0) {
          closureResolve()
        }
      }
    }
    // @ts-ignore
    inProgressAnimations[id] = { stop }
    return () => flipCallbacks[id].onExit!(element, i, stop, decisionData)
  })

  // now append all the fragments from the onExit callbacks
  // (we use fragments for performance)
  fragmentTuples.forEach(t => {
    t[0].appendChild(t[1])
  })

  if (!onExitCallbacks.length) {
    closureResolve!()
  }

  const animateExitingElements = () => {
    onExitCallbacks.forEach(c => c())
    return promiseToReturn
  }

  return {
    hideEnteringElements,
    animateEnteringElements,
    animateExitingElements
  }
}

export default animateUnflippedElements
import { addTupleToObject, getAllElements, getRects } from '../utilities'
import * as constants from '../../../constants'
import { toArray, assign } from '../../../utilities'
import {
  FlippedElementPositionsBeforeUpdateReturnVals,
  FlippedElementPositionDatumBeforeUpdate,
  GetFlippedElementPositionsBeforeUpdateArgs,
  ParentBCRs,
  ChildIdsToParentBCRs,
  ChildIdsToParents
} from './types'
import { InProgressAnimations } from '../../../types'

export const cancelInProgressAnimations = (
  inProgressAnimations: InProgressAnimations,
  animatingElements: HTMLElement[]
) => {
  Object.keys(inProgressAnimations).forEach(id => {
    if (inProgressAnimations[id].destroy) {
      inProgressAnimations[id].destroy!()
    }
    if (inProgressAnimations[id].onAnimationEnd) {
      inProgressAnimations[id].onAnimationEnd!(true)
    }
    delete inProgressAnimations[id]
  })
  animatingElements.forEach(el => {
    el.style.transform = ''
    el.style.opacity = ''
  })
}

const getFlippedElementPositionsBeforeUpdate = ({
  element,
  flipCallbacks = {},
  inProgressAnimations = {},
  portalKey
}: GetFlippedElementPositionsBeforeUpdateArgs): FlippedElementPositionsBeforeUpdateReturnVals => {
  const flippedElements = getAllElements(element, portalKey)

  const inverseFlippedElements = toArray(
    element.querySelectorAll(`[${constants.DATA_INVERSE_FLIP_ID}]`)
  )

  const childIdsToParentBCRs: ChildIdsToParentBCRs = {}
  const parentBCRs: ParentBCRs = []
  const childIdsToParents: ChildIdsToParents = {}
  // this is for exit animations so we can re-insert exiting elements in the
  // DOM later
  flippedElements
    .filter(
      el =>
        flipCallbacks &&
        flipCallbacks[el.dataset.flipId!] &&
        flipCallbacks[el.dataset.flipId!].onExit
    )
    .forEach(el => {
      let parent = el.parentNode as HTMLElement
      // this won't work for IE11
      if (el.closest) {
        const exitContainer = el.closest(
          `[${constants.DATA_EXIT_CONTAINER}]`
        ) as HTMLElement
        if (exitContainer) {
          parent = exitContainer
        }
      }
      let bcrIndex = parentBCRs.findIndex(n => n[0] === parent)
      if (bcrIndex === -1) {
        parentBCRs.push([parent, parent.getBoundingClientRect()])
        bcrIndex = parentBCRs.length - 1
      }
      childIdsToParentBCRs[el.dataset.flipId!] = parentBCRs[bcrIndex][1]
      childIdsToParents[el.dataset.flipId!] = parent
    })

  const filteredFlippedElements = getRects(flippedElements)

  const flippedElementPositionsTupleArray: [
    string,
    FlippedElementPositionDatumBeforeUpdate
  ][] = filteredFlippedElements.map(([child, childBCR]) => {
    const domDataForExitAnimations = {}

    // only cache extra data for exit animations
    // if the element has an onExit listener
    if (
      flipCallbacks &&
      flipCallbacks[child.dataset.flipId!] &&
      flipCallbacks[child.dataset.flipId!].onExit
    ) {
      const parentBCR = childIdsToParentBCRs[child.dataset.flipId!]

      assign(domDataForExitAnimations, {
        element: child,
        parent: childIdsToParents[child.dataset.flipId!],
        childPosition: {
          top: childBCR.top - parentBCR.top,
          left: childBCR.left - parentBCR.left,
          width: childBCR.width,
          height: childBCR.height
        }
      })
    }

    return [
      child.dataset.flipId!,
      {
        rect: childBCR,
        opacity: parseFloat(window.getComputedStyle(child).opacity || '1'),
        domDataForExitAnimations
      }
    ]
  }) as [string, FlippedElementPositionDatumBeforeUpdate][]

  const flippedElementPositions = flippedElementPositionsTupleArray.reduce(
    addTupleToObject,
    {}
  )

  // do this at the very end since we want to cache positions of elements
  // while they are mid-transition
  cancelInProgressAnimations(
    inProgressAnimations,
    flippedElements.concat(inverseFlippedElements)
  )

  return {
    flippedElementPositions,
    cachedOrderedFlipIds: filteredFlippedElements.map(
      ([el]) => el.dataset.flipId!
    )
  }
}

export default getFlippedElementPositionsBeforeUpdate
import onFlipKeyUpdate from './flip'
import getFlippedElementPositionsBeforeUpdate from './flip/getFlippedElementPositions/getFlippedElementPositionsBeforeUpdate'
import { assign } from './utilities'
import {
  StaggerConfig,
  HandleEnterUpdateDelete,
  InProgressAnimations,
  FlipCallbacks,
  OnFlipperComplete,
  FlippedProps,
  OnFlipperStart
} from './types'
import { SpringOption } from './springSettings/types'
import { FlippedElementPositionsBeforeUpdate } from './flip/getFlippedElementPositions/getFlippedElementPositionsBeforeUpdate/types'
import { FlippedIds } from './flip/types'

interface Options {
  element: HTMLElement
  staggerConfig?: StaggerConfig
  spring?: SpringOption
  applyTransformOrigin?: boolean
  handleEnterUpdateDelete?: HandleEnterUpdateDelete
  debug?: boolean
  onStart?: OnFlipperStart
  onComplete?: OnFlipperComplete
}

class Flipper {
  private element!: HTMLElement
  private staggerConfig!: StaggerConfig
  private applyTransformOrigin = true
  private handleEnterUpdateDelete!: HandleEnterUpdateDelete
  private debug?: boolean
  private spring!: SpringOption
  private inProgressAnimations: InProgressAnimations
  private flipCallbacks: FlipCallbacks
  private snapshot?: {
    flippedElementPositions: FlippedElementPositionsBeforeUpdate
    cachedOrderedFlipIds: FlippedIds
  }
  private onComplete?: OnFlipperComplete
  private onStart?: OnFlipperStart

  constructor(options: Options) {
    assign(this, options)

    this.inProgressAnimations = {}
    this.flipCallbacks = {}

    this.recordBeforeUpdate = this.recordBeforeUpdate.bind(this)
    this.update = this.update.bind(this)
    this.addFlipped = this.addFlipped.bind(this)
    this.addInverted = this.addInverted.bind(this)
  }

  recordBeforeUpdate() {
    this.snapshot = getFlippedElementPositionsBeforeUpdate({
      element: this.element,
      flipCallbacks: this.flipCallbacks,
      inProgressAnimations: this.inProgressAnimations
    })
  }
  update(prevDecisionData: any, currentDecisionData: any) {
    if (this.snapshot) {
      onFlipKeyUpdate({
        flippedElementPositionsBeforeUpdate:
          this.snapshot.flippedElementPositions,
        cachedOrderedFlipIds: this.snapshot.cachedOrderedFlipIds,
        containerEl: this.element,
        inProgressAnimations: this.inProgressAnimations,
        flipCallbacks: this.flipCallbacks,
        applyTransformOrigin: this.applyTransformOrigin,
        spring: this.spring,
        debug: this.debug,
        staggerConfig: this.staggerConfig,
        handleEnterUpdateDelete: this.handleEnterUpdateDelete,
        decisionData: {
          previous: prevDecisionData,
          current: currentDecisionData
        },
        onComplete: this.onComplete,
        onStart: this.onStart
      })
      delete this.snapshot
    }
  }

  addFlipped({
    element,
    flipId,
    opacity,
    translate,
    scale,
    transformOrigin,
    spring,
    stagger,
    delayUntil,
    onAppear,
    onStart,
    onSpringUpdate,
    onComplete,
    onExit,
    shouldFlip,
    shouldInvert
  }: FlippedProps & { element: HTMLElement }) {
    if (!element) {
      throw new Error('no element provided')
    }
    if (!flipId) {
      throw new Error('No flipId provided')
    }
    const flipConfig = {
      scale,
      translate,
      opacity,
      transformOrigin,
      spring,
      stagger,
      delayUntil
    }
    if (!flipConfig.scale && !flipConfig.translate && !flipConfig.opacity) {
      assign(flipConfig, {
        translate: true,
        scale: true,
        opacity: true
      })
    }
    if (flipId) {
      element.dataset.flipId = String(flipId)
    }
    element.dataset.flipConfig = JSON.stringify(flipConfig)
    // finally, add callbacks
    this.flipCallbacks[flipId] = {
      shouldFlip,
      shouldInvert,
      onAppear,
      onStart,
      onSpringUpdate,
      onComplete,
      onExit
    }
  }

  addInverted({
    element,
    parent,
    opacity,
    translate,
    scale,
    transformOrigin
  }: {
    element: HTMLElement
    parent: HTMLElement
    opacity: boolean
    translate: boolean
    scale: boolean
    transformOrigin: string
  }) {
    if (!element) {
      throw new Error('no element provided')
    }
    if (!parent) {
      throw new Error('parent must be provided')
    }

    const inverseFlipId = parent.dataset.flipId
    const flipConfig = {
      scale,
      translate,
      opacity,
      transformOrigin
    }
    if (!flipConfig.scale && !flipConfig.translate && !flipConfig.opacity) {
      assign(flipConfig, {
        translate: true,
        scale: true,
        opacity: true
      })
    }
    element.dataset.inverseFlipId = inverseFlipId
    element.dataset.flipConfig = JSON.stringify(flipConfig)
  }
}

export default Flipper
// this is exclusively for users of the library to create their own enter + exit animations
import { SpringSystem } from '../forked-rebound'
import { SpringSystemInterface } from '../forked-rebound/types.d'
import { tweenProp, assign } from '../utilities'
import { normalizeSpring, springPresets } from '../springSettings'
import { SimpleSpringOptions } from './types'
import { SpringConfig } from '../springSettings/types'

// this should get created only 1x
const springSystem: SpringSystemInterface = new SpringSystem()

/**
 * A simple spring function for animating DOM properties.
 * Returns a function that will immediately cancel the in-progress animation.
 * */
const createSimpleSpring = ({
  config,
  values,
  onUpdate,
  delay = 0,
  onComplete
}: SimpleSpringOptions) => {
  const { stiffness, damping, overshootClamping } = assign(
    {},
    springPresets.noWobble,
    normalizeSpring(config)
  ) as SpringConfig
  const spring = springSystem.createSpring(stiffness!, damping!)
  spring.setOvershootClampingEnabled(!!overshootClamping)
  spring.addListener({
    onSpringAtRest: spring => {
      if (onComplete) onComplete()
      spring.destroy()
    },
    onSpringUpdate: spring => {
      const springVal = spring.getCurrentValue()
      if (!values) return onUpdate(springVal)
      const currentValues = Object.keys(values)
        .map(value => [
          value,
          tweenProp(values[value][0], values[value][1], springVal)
        ])
        .reduce((acc, curr) => {
          return Object.assign(acc, { [curr[0]]: curr[1] })
        }, {})
      onUpdate(currentValues)
    }
  })
  if (delay) {
    setTimeout(() => {
      spring.setEndValue(1)
    }, delay)
  } else {
    spring.setEndValue(1)
  }
  return spring
}

export default createSimpleSpring
